import classes from "./ProfileForm.module.css";
import { useRef, useContext } from "react";
import AuthContext from "../../store/auth-context";

const ProfileForm = () => {
  const newPassworInputRef = useRef();
  const authCtx = useContext(AuthContext);

  const submitHandler = (event) => {
    event.preventDefault();

    const password = newPassworInputRef.current.value;

    // Can add validation here to check password
    const url =
      "https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyDjMITcryuUpecny4vHV9jgzmlqZbrsPIc";
    const changePassword = async () => {
      const response = await fetch(url, {
        method: "POST",
        body: JSON.stringify({
          idToken,
          password,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      });
    };

    changePassword();
  };
  return (
    <form className={classes.form} onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="new-password">New Password</label>
        <input ref={newPassworInputRef} type="password" id="new-password" />
      </div>
      <div className={classes.action}>
        <button>Change Password</button>
      </div>
    </form>
  );
};

export default ProfileForm;
