import { createSlice } from "@reduxjs/toolkit";

const initialUIState = { showCart: false };

const ui = createSlice({
  name: "ui",
  initialState: initialUIState,
  reducers: {
    toggle(state) {
      state.showCart = !state.showCart;
    },
  },
});

export const uiAction = ui.actions;

export default ui;
